import React from 'react';
import styled from 'styled-components';
import * as dateFns from 'date-fns';
import { breakpoints } from 'styled-bootstrap-responsive-breakpoints';
import { Container, Row, Col } from 'react-bootstrap';
import scissorsLeft from '../../pictures/Background2.1.png';
import scissorsRight from '../../pictures/Background2.png';

const Styles = styled.div`
    
`


export default function CalendarHeader(props) {

    return (
        <Styles>
            <Container>
                <Row className="text-center align-items-center">
                    <Col>
                        <i class="fa fa-chevron-left" aria-hidden="true" onClick={props.prevMonth}></i>
                    </Col>
                    <Col>
                        <span>
                            <h4>{dateFns.format(props.actualMonth, "MMMM yyyy")}</h4>
                        </span>
                    </Col>
                    <Col>
                        <img src={scissorsRight} className="w-50" onClick={props.nextMonth}></img>
                    </Col>
                </Row>
            </Container>
        </Styles>
    );
}
