import React, { useState, useEffect } from 'react';

class Customers extends React.Component {
    constructor() {
        super();
        this.state = {
            customers: []      
        }
    }


    componentDidMount() {
        fetch("/api/customers")
        .then(res => res.json())
        .then(customers => this.setState({customers}, () => console.log("Customers fetched..", customers)))
    }

    render() {
      return (
        <h1>customers</h1>

      ) 
    }
  }

  export default Customers;

    
