import React, { useState } from 'react';
import { Modal } from 'react-bootstrap';
import FadeIn from "react-fade-in";
import Lottie from "react-lottie";
import * as loader from "../../animations/splashy-loader.json";
import styled from 'styled-components';


const defaultOptions = {
  loop: true,
  autoplay: true,
  animationData: loader.default,
  rendererSettings: {
    preserveAspectRatio: "xMidYMid slice"
  }
};

const StyledModal = styled(Modal)`
`

const Styles = styled.div`

#eeeeee;

`

export default function Thanks(props) {

  const [state, setState] = useState({
    done: false,
    modalShow: false
  });

 

function ThanksModal(props) {

  if (props.ready) {
    setState({ 
      done: state.done,
      modalShow: true
     })
    setTimeout(() => {
      setState({ 
        done: true,
        modalShow: state.modalShow
       })
      setTimeout(() => {
        window.location = '/';
      }, 2500)
    }, 2000);

  }

  return (
<StyledModal
          size='lg'
          {...props}
          aria-labelledby="contained-modal-title-vcenter"
        >
          <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
              <h3 className="my-0">Choose your term</h3>
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
          {!state.done ? (
            <div className="d-flex justify-content-center align-items-center">
              <Lottie options={defaultOptions} height={120} width={120} />
            </div>
            ) : (
              <FadeIn>
                <h1>Thank you for choosing our barbershop!</h1>
              </FadeIn>
            )}
          </Modal.Body>
          <Modal.Footer>

          </Modal.Footer>
        </StyledModal>
  )
}

  return (
    <Styles className={`${props.className} text-center`}>
        <ThanksModal ready={props.ready} show={state.modalShow} />

      
    </Styles>
  );
}
