import React, { useState } from 'react';
import styled from 'styled-components';
import * as dateFns from 'date-fns';
import { breakpoints } from 'styled-bootstrap-responsive-breakpoints';
import { Container, Row, Col } from 'react-bootstrap';


const Styles = styled.div`

`

export default function Calendar() {

    const [state, setState] = useState({
        currentMonth: new Date(),
        selectedDate: new Date()
    });

    const currentDate = new Date();

    console.log();


    let renderHeader = () => {
        const dateFormat = "MMMM yyyy";

        return (
            <Container>
                <Row className="text-center">
                    <Col>
                        <i class="arrow far fa-caret-square-left" onClick={prevMonth}></i>
                    </Col>
                    <Col>
                        <span>
                            {dateFns.format(state.currentMonth, dateFormat)}
                        </span>
                    </Col>
                    <Col>
                        <i class="arrow far fa-caret-square-right" onClick={nextMonth}></i>
                    </Col>
                </Row>
            </Container>
        );
    }

    let renderDays = () => {
        const dateFormat = "iiii";
        const days = [];

        let startDate = dateFns.startOfWeek(state.currentMonth);

        for (let i = 0; i < 7; i++) {
            days.push(
                <Col key={i}>
                    {dateFns.format(dateFns.addDays(startDate, i + 1), dateFormat)}
                </Col>
            );
        }

        return (
            <Container>
                <Row className="text-center">{days}</Row>
            </Container>
        );

    }

    let renderCells = () => {
        const { currentMonth, selectedDate } = state;
        const monthStart = dateFns.startOfMonth(currentMonth);
        const monthEnd = dateFns.endOfMonth(monthStart);
        const startDate = dateFns.startOfWeek(monthStart, { weekStartsOn: 1 });
        const endDate = dateFns.endOfWeek(monthEnd, { weekStartsOn: 1 });

        const dateFormat = "d";
        const rows = [];
        let days = [];
        let day = startDate;
        let formattedDay = "";


        for (day; day <= endDate; day++) {
            for (let i = 0; i < 7; i++) {
                formattedDay = dateFns.format(day, dateFormat);
                days.push(
                    <Col className={` ${dateFns.isSameMonth(day, monthStart) ? "" : "text-muted"} `} key={day}>
                        <span>{formattedDay}</span>
                    </Col>
                );
                day = dateFns.addDays(day, 1);
            }
            rows.push(
                <Row className="text-center" key={day}>
                    {days}
                </Row>
            );
            days = [];
        }
        return <div>{rows}</div>
    }

    let onDateClick = day => { }

    let nextMonth = () => {
        setState({
            currentMonth: dateFns.addMonths(state.currentMonth, 1)
        });
    }

    let prevMonth = () => {
        if (dateFns.differenceInMonths(state.currentMonth, currentDate) >= 0) {
            setState({
                currentMonth: dateFns.subMonths(state.currentMonth, 1)
            });
        }
    }


    return (
        <Styles>
            {renderHeader()}
            {renderDays()}
            {renderCells()}
        </Styles>
    );
}
