import React from 'react';
import { Modal, Button } from 'react-bootstrap';
import { breakpoints } from 'styled-bootstrap-responsive-breakpoints';
import Calendar from './Calendar';
import styled from 'styled-components';

const Styles = styled.div`

.my-modal-header {
    background-color: #d65a31;
}
.my-modal-body {
    background-color: #eeeeee;
}
`
if (props.show) {
    var element1 = document.getElementById("modalHeader");
    element1.classList.add("my-modal-header");
    var element2 = document.getElementById("modalBody");
    element2.classList.remove("my-modal-body");
}

export default function BookWindow(props) {
    return (
        <Styles>
            <Modal size='lg' show={props.show} aria-labelledby="contained-modal-title-vcenter">
                <Modal.Header id="modalHeader" closeButton modalClassName="my-modal-header">
                    <Modal.Title id="contained-modal-title-vcenter">
                        Schedule
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body id="modalBody">
                    <Calendar />
                </Modal.Body>
                <Modal.Footer>
                    <Button onClick={props.onHide}>Close</Button>
                </Modal.Footer>
            </Modal>
        </Styles>
    );
}
