import React from 'react';
import { Container, Col, Row,} from 'react-bootstrap';
import pole from '../../pictures/pole.png';
import styled from 'styled-components';
import { breakpoints } from 'styled-bootstrap-responsive-breakpoints';

const Styles = styled.div`

@media (min-width: ${breakpoints.xs}) {
    .container-fluid {
        padding: 0 0 4em 0;
        h1 {
            font-size: 2.5em;
            text-shadow: 0.25vw 0.25vw #000;
        }
        img {
            max-width: 1.25em;
            filter: drop-shadow(0.3vw 0.3vw #000);
        }    
    }  
  }  

  @media(min-width: ${breakpoints.sm}) {
    .container-fluid {
        h1 {
            font-size: 3.5em;
        }
        img {
            max-width: 2em;
        }    
    }
  }

  @media(min-width: ${breakpoints.md}) {

    .container-fluid {
        padding: 2em 0 7em 0;
        
        h1 {
          font-size: 4em;       
        }
      
        img {
         max-width: 2.5em; 
        }
      }
    }


`

export default function SectionHeader(props) {
    return (
        <Styles className="text-white">
            <Container fluid={true} className="">
                <Row className="justify-content-center align-items-center ">
                    <Col className="text-right" >
                        <img
                            src={pole}
                            alt="pole"
                        />
                    </Col>
                    <Col sm={5} md={4} lg={3}  className="text-center px-0 mx-0 px-sm-3 px-md-0" >
                        <h1 className="d-inline">{props.name}</h1>
                    </Col>
                    <Col className="text-left ">
                        <img
                            src={pole}
                            alt="pole"
                        />
                    </Col>
                </Row>

            </Container>
        </Styles>
    );
}
