import React from 'react';
import { Navbar, Nav } from 'react-bootstrap';
import { breakpoints } from 'styled-bootstrap-responsive-breakpoints';
import styled from 'styled-components';


const Styles = styled.div`

  .navbar {
    background-color: #000;
  }

  .navbar-nav {
    margin: 0 2em;
  }

  @media(min-width: ${breakpoints.xs}) {
    h1 {
      font-size: 1.5em;
    }
    .nav-link {
      font-size: 1.5em;
    }
  }

  @media(min-width: ${breakpoints.lg}) {
    h1 {
      display: inline;
      font-size: 2em;
    }
    .nav-link {
      font-size: 1.5em;
      margin: 0 0.25em;
    }
}
` 

let scrollDown = () => {
  setTimeout(() => {
    window.scrollBy({
      top: 100,
      behavior: "smooth"
    })
  }, 500)
}

export default function Navigation() {

  return (
    <Styles id="navbar">
      <Navbar variant="dark" expand="md" fixed="top" collapseOnSelect>
        <Navbar.Brand href="#home">
          <h1>Barber Andrew</h1>
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="mr-auto">
            <Nav.Link href="#home" onClick={scrollDown}>Home</Nav.Link>
            <Nav.Link href="#services" onClick={scrollDown}>Sevices</Nav.Link>
            <Nav.Link href="#contact" onClick={scrollDown}>Contact</Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Navbar>
    </Styles>
  );
}
