import React, { useState, useEffect } from 'react';


export default function Customers() {

    const [state, setState] = useState({
        customers: []
    })

    useEffect(() => {

        fetch("/barber", {method: "POST"}) 
        .then(res => res.json())
        .then(customers => setState({customers: customers}))


    },[]);

    return (
        <h1>customers</h1>
    );
}

