import React, { useState, useEffect } from 'react';
import FadeIn from "react-fade-in";
import Lottie from "react-lottie";
import * as loader from "../../animations/splashy-loader.json";
import * as tick from "../../animations/red-tick.json";
import styled from 'styled-components';
import axios from 'axios';
import { breakpoints } from 'styled-bootstrap-responsive-breakpoints';

const defaultOptions = {
  loop: true,
  autoplay: true,
  animationData: loader.default,
  rendererSettings: {
    preserveAspectRatio: "xMidYMid slice"
  }
};
const defaultOptions2 = {
  loop: false,
  autoplay: true,
  animationData: tick.default,
  rendererSettings: {
    preserveAspectRatio: "xMidYMid slice"
  }
};

const Styles = styled.div`

#eeeeee;

`

export default function Thanks(props) {

    const [state, setState] = useState({
        loading: undefined,
        done: undefined
    });

    useEffect(() => {
      setTimeout(() => {
        fetch("https://jsonplaceholder.typicode.com/posts")
          .then(response => response.json())
          .then(json => {
            setState({ loading: true });
            setTimeout(() => {
              setState({ done: true });
            }, 1000);
          });
      }, 1200);
    })

    return (
      <Styles className={`${props.className} text-center`}>
          {!state.done ? (
          <FadeIn>
            <div class="d-flex justify-content-center align-items-center">
              {!state.loading ? (
                <Lottie options={defaultOptions} height={120} width={120} />
              ) : (
                <Lottie options={defaultOptions2} height={120} width={120} />
              )}
            </div>
          </FadeIn>
        ) : (
          <h1>Thank you for choosing our barbershop!</h1>
        )}
      </Styles>  
    );
}
