import React, { useState } from 'react';
import { Navbar, Nav } from 'react-bootstrap';
import { breakpoints } from 'styled-bootstrap-responsive-breakpoints';
import styled from 'styled-components';


const Styles = styled.div`

  .navbar {
    background-color: #000;
  }

  .navbar-nav {
    margin: 0 2em;
  }

  @media(min-width: ${breakpoints.xs}) {
    h1 {
      font-size: 1.5em;
    }
    .nav-link {
      font-size: 1.5em;
    }
  }

  @media(min-width: ${breakpoints.lg}) {
    h1 {
      display: inline;
      font-size: 2em;
    }
    .nav-link {
      font-size: 1.5em;
      margin: 0 0.25em;
    }
}
`


export default function Navigation() {

  const [id, setId] = useState("");

  let scrollDown = section => {
    setId(section);
    let element = document.getElementById(id);
    if (element.length > 0) {
      window.scrollTo({
        top: element.offsetTop - 50,
        behavior: "smooth"
      })
    }
  
  }

  return (
    <Styles id="navbar">
      <Navbar variant="dark" expand="md" fixed="top" collapseOnSelect>
        <Navbar.Brand href="#home">
          <h1>Barber Andrew</h1>
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="mr-auto">
            <Nav.Link href="" onClick={scrollDown("home")}>Home</Nav.Link>
            <Nav.Link href="" onClick={scrollDown("services")} >Sevices</Nav.Link>
            <Nav.Link href="" onClick={scrollDown("contact")} >Contact</Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Navbar>
    </Styles>
  );
}
