import React from 'react';
import { Navbar, Nav } from 'react-bootstrap';
import { breakpoints } from 'styled-bootstrap-responsive-breakpoints';
import styled from 'styled-components';


const Styles = styled.div`

  .navbar {
    background-color: #000;
  }

  .navbar-nav {
    margin: 0 2em;
  }

  @media(min-width: ${breakpoints.xs}) {
    h1 {
      font-size: 1.5em;
    }
    .nav-link {
      font-size: 1.5em;
    }
  }

  @media(min-width: ${breakpoints.lg}) {
    h1 {
      display: inline;
      font-size: 2em;
    }
    .nav-link {
      font-size: 1.5em;
      margin: 0 0.25em;
    }
}
`

export default function Navigation() {

  let offset = elmnt => {
    window.scrollTo({
      top: elmnt.offsetTop - 50,
      behavior: "smooth"
    })
  }

  let scrollHome = () => {
    let elmnt = document.getElementById("home");
    offset(elmnt);
  }
  
  let scrollServices = () => {
    let elmnt = document.getElementById("services");
    offset(elmnt);
  }
  let scrollContact = () => {
    let elmnt = document.getElementById("contact");
    offset(elmnt);
  }

  return (
    <Styles id="navbar">
      <Navbar collapseOnSelect variant="dark" expand="md" fixed="top">
        <Navbar.Brand href="#home">
          <h1>Barber Andrew</h1>
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="responsive-navbar-nav" />
        <Navbar.Collapse id="responsive-navbar-nav">
          <Nav className="mr-auto">
            <Nav.Link href="#home" onClick={scrollHome} >Home</Nav.Link>
            <Nav.Link href="#services" onClick={scrollServices} >Sevices</Nav.Link>
            <Nav.Link href="#contact" onClick={scrollContact} >Contact</Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Navbar>
    </Styles>
  );
}
