import React from 'react';
import SectionHeader from './SectionHeader';
import ServiceCard from './ServiceCard';
import { Card, CardColumns } from 'react-bootstrap';
import styled from 'styled-components';
import services from '../services';
import { breakpoints } from 'styled-bootstrap-responsive-breakpoints';
import razor from './pictures/razor3.png';
import both from './pictures/both.png';

const Styles = styled.div`

background: #222831;
padding: 0 0 10vw 0;

.card {
  font-size: 4em;
   
}
.h5 {
  font-size: 0.55em;
}

.card-img-top {
  width: 5em;
}

.card-body {
  padding-bottom: 0;
}

`
function createCard(service) {
  return (
    <ServiceCard
      key={service.id}
      img={service.img}
      name={service.name}
      price={service.price}
    />
    

  );
}


function Services() {
  return (
    <Styles id="services" className="p-5">
      <SectionHeader name="Services" />
      <CardColumns className="w-75 mx-auto">
        {/* {services.map(createCard)} */}
          <Card className="p-3 text-center">
            <Card.Img variant="top" src='../pictures/scissors3.png' />
            <Card.Body>
              <Card.Title>Scissor Cut And Style</Card.Title>
              <Card.Text>
              £28 
                </Card.Text>
            </Card.Body>
          </Card>
          <Card className="p-3 text-center">
            <Card.Img variant="top" src={razor} />
            <Card.Body>
              <Card.Title>Beard Trim With Razor</Card.Title>
              <Card.Text>
              £18
                </Card.Text>
            </Card.Body>
          </Card>
          <Card className="p-3 text-center">
            <Card.Img variant="top" src={both} />
            <Card.Body>
              <Card.Title>Scissor Cut + Beard Trim</Card.Title>
              <Card.Text>
              £40
                </Card.Text>
            </Card.Body>
          </Card>
      </CardColumns>
    </Styles>
  );



}

export default Services;
