import React, { useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import * as dateFns from 'date-fns';
import { breakpoints } from 'styled-bootstrap-responsive-breakpoints';
import { Button, Form } from 'react-bootstrap';


const Styles = styled.div`

`

export default function BookForm(props) {

    const [contact, setContact] = useState({
        customerName: "",
        customerPhone: "",
        date: dateFns.format(props.date,"dd-MM-y"),
        hourKey: props.id
    });

    console.log(contact.date);

    let handleChange = event => {
        const { value, name } = event.target;

        setContact (prevValue => {
            if (name === "customerName") {
                return {
                    customerName: value,
                    customerPhone: prevValue.customerPhone,
                    date: props.date,
                    hourKey: prevValue.hourKey
                }
            } else {
                return {
                    customerName: prevValue.customerName,
                    customerPhone: value,
                    date: props.date,
                    hourKey: prevValue.hourKey
                }
            }
        });
    }

    let handleSubmit = e => {
        e.preventDefault();

        const contactData = {
            name: contact.customerName,
            phone: contact.customerPhone,
            date: contact.date,
            key: contact.hourKey
        }

        console.log(contactData);

        axios.post('http://localhost:5000/barber/', contactData)
      .then(res => console.log(res.data));
    }

    return (
        <Styles>
            <Form onSubmit={handleSubmit}>
                <Form.Group controlId="formBasicName">
                    <Form.Label>Your name</Form.Label>
                    <Form.Control 
                        type="text"
                        name="customerName" 
                        placeholder="Name" 
                        onChange={handleChange}
                        value={contact.customerName}                        
                    />
                </Form.Group>
                <Form.Group controlId="formBasicPhone">
                    <Form.Label>Your phone number</Form.Label>
                    <Form.Control
                        type="text" 
                        name="customerPhone" 
                        placeholder="Enter phone"
                        onChange={handleChange} 
                        value={contact.customerPhone} 
                    />  
                </Form.Group>
                <Button 
                    variant="primary" 
                    type="submit"
                >
                    Book visit!     
                </Button>
            </Form>
        </Styles>
    );
}
