import React from 'react';
import styled from 'styled-components';
import * as dateFns from 'date-fns';
import { breakpoints } from 'styled-bootstrap-responsive-breakpoints';
import { Container, Row, Col } from 'react-bootstrap';
import circle from '../../pictures/red-circle.png'

const Styles = styled.div`

@media (min-width: ${breakpoints.xs}) {
    .my-col {
        overflow: visible;
    }
    
    .cell {
        font-family: 'Anton', sans-serif;
        font-size: 1.25em;
    }

    .choosen-cell {
        background: #a5adc0;
        color: #fff;
    }
    .avilable-cell {
    &:hover {
        cursor: pointer;
        // background: url(${circle});
        // background-size: 150%;
        // background-repeat: no-repeat;
        color: #fff;
    }
    circle {
        width: 50%;
        position: absolute;
        left: 0px;
        top: 0px;
        //z-index: 2;
    }
}

}

@media (min-width: ${breakpoints.md}) {
    .cell {
        font-size: 1.5em;
}    


`
export default function CalendarCells(props) {

    const { currentMonth, selectedDate, currentDate } = props.state;
    const monthStart = dateFns.startOfMonth(currentMonth);
    const monthEnd = dateFns.endOfMonth(monthStart);
    const startDate = dateFns.startOfWeek(monthStart, { weekStartsOn: 1 });
    const endDate = dateFns.endOfWeek(monthEnd, { weekStartsOn: 1 });

    const rows = [];

    let days = [];
    let day = startDate;
    let formattedDate = "";


    for (day; day <= endDate; day++) {
        for (let i = 0; i < 7; i++) {
            formattedDate = dateFns.format(day, "d");
            const cloneDay = day;
            days.push(
                <Col
                    className={`${dateFns.isSameDay(day, selectedDate)
                        ? "choosen-cell"
                        : dateFns.isSameMonth(day, monthStart) ? "" : "text-muted"} 
                        ${dateFns.isSameDay(currentDate, day) | dateFns.isBefore(currentDate, day)
                        ? "avilable-cell" : ""}
                        px-0  border rounded my-col
                        `}

                    key={day}
                    onClick={() => props.onDateClick(cloneDay)}
                >
                    <span className="cell">{formattedDate}</span>
                    <img src={circle} className="circle"></img>
                </Col>
            );
            day = dateFns.addDays(day, 1);
        }
        rows.push(
            <Row className="text-center" key={day}>
                {days}
            </Row>
        );
        days = [];
    }

    return (
        <Styles>
            <Container>{rows}</Container>
        </Styles>
    );
}
