import React from 'react';
import SectionHeader from '../SectionHeader';
import styled from 'styled-components';
import MapContainer from './MapContainer';
import BookButton from '../../BookButton';
import { breakpoints } from 'styled-bootstrap-responsive-breakpoints';

const Styles = styled.div`

background: #d65a31;


@media(max-width: ${breakpoints.md}) {

  h1 {
    font-size: 2em;
  }
  h2 {
    font-size: 1.5em;
  }
} 

`

export default function Contact() {
  return (
    <Styles id="contact" className="py-5 text-center">
      <SectionHeader name="Contact" />
      
      <h1>Phone:</h1>
      <h2>Call now and book your visit!</h2>
      <h2 className="pb-5">659 852 666</h2>

      <h1>E-mail:</h1>
      <h2 className="pb-5">info@barber-andrew.com</h2>

      <h1>Opening Times:</h1>
      <h2>Monday – Friday: 9.00 – 17.30</h2>
      <h2>Saturday: 9.00 – 14.00</h2>
      <h2 className="pb-5">Sunday: Closed</h2>

      <MapContainer />

      <BookButton name="Book your visit now!" />

    </Styles>
  );



}
