import React from 'react';
import { Container, Col, Row } from 'react-bootstrap';
import scissors from '../pictures/Background.png';
import razor from '../pictures/straight-razor.png';
import styled from 'styled-components';
import { breakpoints } from 'styled-bootstrap-responsive-breakpoints';

const Styles = styled.div`
h3 {
  color: black;
}

`

function Services() {
  return (
    <Styles id="services">
      <Container fluid={true}>
        <Row>
          <Col lg={4}>
            <img
              className="d-block w-100"
              src={scissors}
              alt="scissors"
            />
            <h3>Scissors Cut And Style</h3>
          </Col>
          <Col lg={4}>
            <img
              className="d-block w-100"
              src={razor}
              alt="razor"
            />
            <h3>Beard Trim With Razor</h3>
          </Col>
          <Col lg={4}>
            <img
              className="d-block w-100"
              src={scissors}
              alt="scissors"
            />
            <img
              className="d-block w-100"
              src={razor}
              alt="razor"
            />
            <h3>Both</h3>
          </Col>
        </Row>
      </Container>
    </Styles>
  );



}

export default Services;
