import React from 'react';
import styled from 'styled-components';
import logo from '../pictures/head.png';
import { breakpoints } from 'styled-bootstrap-responsive-breakpoints';
import Socials from "./Socials";
import { Container } from 'react-bootstrap';

const Styles = styled.div`

background: #eeeeee;

@media (min-width: ${breakpoints.xs}) {
  height: 12em;
  img {
    width: 5em;
  }

  .small-p {
    display: block;
  }
  .normal
}


`

function Footer() {
  return (
    <Styles >
      <div className="d-flex justify-content-around align-items-center " style={{zIndex:1}}>
      <div className="d-flex align-items-center">
          <h1 className="">Barber</h1>
          <img
            className="p-3"
            src={logo}
            alt="logo"
          />
          <h1>Andrew</h1>
        </div>
        <p className="d-none d-md-block">© Copyright 2020 Barber Andrew</p>
        <Socials />
      </div>
      <p className="position-relative text-center d-block d-md-none" style={{zIndex:2}}>© Copyright 2020 Barber Andrew</p>
    </Styles>
  );



}

export default Footer;
