import React, { useState } from 'react';
import styled from 'styled-components';
import { Button } from 'react-bootstrap';
import { breakpoints } from 'styled-bootstrap-responsive-breakpoints';
import BookWindow from './BookWindow/BookWindow';

const Styles = styled.div`


.btn {
    text-shadow: 0.06em 0.06em #000;
    &:hover {
        background-color: rgba(255, 255, 255, 0.3);
        color: white;
    }
  }

  @media (min-width: ${breakpoints.xs}){

    .btn {
      width: 12em;
      margin: 0.2em 0 0 0;
      font-size: 1.2em;  
    }
  }

  @media (min-width: ${breakpoints.sm}){

    .btn {
      width: 12em;
      margin: 2vw 0 0 0;
      font-size: 1.8em;  
    }
  }   

  @media(min-width: ${breakpoints.md}) {

    .btn {
      //width: 13em;
      font-size: 2.5em;
    }
  }

  @media(min-width: ${breakpoints.lg}) {
    .btn {
      width: 40vw;
      font-size: 3.5vw;
    }
  }

  @media(min-width: ${breakpoints.xl}) {
    .btn {
      width: 25vw;
    }
  }

`

export default function BookButton(props) {

  const [modalShow, setModalShow] = useState(false);

    return (
        <Styles className="text-white w-100 mx-auto">
            <Button 
              variant="outline-light" 
              className="Button mx-auto" 
              block 
              size="lg" 
              onClick={() => setModalShow(true)}
            >
                {props.name}
             </Button> 

             <BookWindow show={modalShow} onHide={() => setModalShow(false)} />
        </Styles>
    );
}
