import React from 'react';
import SectionHeader from './SectionHeader';
import { Container, Col, Row, Card, CardColumns } from 'react-bootstrap';
import scissors from '../pictures/scissors.png';
import razor from '../pictures/razor.png';
import back from '../pictures/abstract-art-black.jpg';
import styled from 'styled-components';
import { breakpoints } from 'styled-bootstrap-responsive-breakpoints';

const Styles = styled.div`
.h5, .card-text {
  color: black;
}

.card {
  justify-content
  img {
    width: 20em;
    margin: auto;
  }
}

#services {
  position: relative;
}

#content {
  position: absolute;
  z-index: 2;
  width: 100%;
  
  
}

#opacity {
  background: #222831;
  position: absolute;
  z-index: 1;
  width: 100%;
  height: 100%;
  opacity: 1; 
}

.bg {
  //background: violet;
  //border: 2px solid red;
  width: 70vw;
  margin: auto;
  
}

.prices {
  padding: 5em 0;
  justify-content: space-around;
  border: 2px solid red;
}
`

function Services() {
  return (
    <Styles id="services">
      <div id="opacity"></div>
      <div id="content">
        <SectionHeader name="Services" />
        <div className="bg">

          <CardColumns>
            <Card className="p-3">
              <Card.Img variant="top" src={scissors} />
              <Card.Body>
                <Card.Title>Card title that wraps to a new line</Card.Title>
                <Card.Text>
                  This is a longer card with supporting text below as a natural lead-in to
                  additional content. This content is a little bit longer.
                </Card.Text>
              </Card.Body>
            </Card>
            <Card>
              <Card.Img variant="top" src={razor} />
              <Card.Body>
                <Card.Title>Card title that wraps to a new line</Card.Title>
                <Card.Text>
                  This is a longer card with supporting text below as a natural lead-in to
                  additional content. This content is a little bit longer.
                </Card.Text>
              </Card.Body>
            </Card>
            <Card>
              <Card.Img variant="top" src={scissors} />
              <Card.Body>
                <Card.Title>Card title that wraps to a new line</Card.Title>
                <Card.Text>
                  This is a longer card with supporting text below as a natural lead-in to
                  additional content. This content is a little bit longer.
                </Card.Text>
              </Card.Body>
            </Card>
            </CardColumns>
            {/* <Container className="prices" fluid={true}>
            <Row className="justify-content-center">
              <Col lg={3}>
                 <Card>
                <Card.Img variant="top" src={scissors} />
                <Card.Body>
                  <Card.Title>Scissors Cut And Style</Card.Title>
                  <Card.Text>
                    This is a longer card with supporting text below as a natural lead-in to
                    additional content. This content is a little bit longer.
                </Card.Text>
                </Card.Body>
              </Card>
                <img
                  className="d-block w-100"
                  src={scissors}
                  alt="scissors"
                />
                <h3>Scissors Cut And Style</h3>
              </Col>
              <Col lg={3}>
                <img
                  className="d-block w-100"
                  src={razor}
                  alt="razor"
                />
                <h3>Beard Trim With Razor</h3>
              </Col>
              <Col lg={3}>
                <img
                  className="d-block w-100"
                  src={scissors}
                  alt="scissors"
                />
                <img
                  className="d-block w-100"
                  src={razor}
                  alt="razor"
                />
                <h3>Both</h3>
              </Col>
            </Row>
          </Container> */}
        </div>
        </div>
    </Styles>
  );



}

export default Services;
