import React from 'react';
import SectionHeader from './SectionHeader';
import { Container, Col, Row, Card } from 'react-bootstrap';
import scissors from '../pictures/Background.png';
import razor from '../pictures/straight-razor.png';
import wood from '../pictures/close-up-of-wooden-plank.jpg';
import styled from 'styled-components';
import { breakpoints } from 'styled-bootstrap-responsive-breakpoints';

const Styles = styled.div`
h3, h5 .card {
  color: black;
}

section::before {
  content: "";
  position: absolute;
  top: 0; 
  left: 0;
  width: 100%;
  height: 100%;
  z-index: -1;
  opacity: .5; 
  background-image: url(${wood});
}

`

function Services() {
  return (
    <Styles id="services">
     <SectionHeader name="Services" />
      <Container fluid={true}>
        <Row className="justify-content-center">
          <Col lg={3}>
            <Card>
              <Card.Img variant="top" src={scissors} />
              <Card.Body>
                <Card.Title>Scissors Cut And Style</Card.Title>
                <Card.Text>
                  This is a longer card with supporting text below as a natural lead-in to
                  additional content. This content is a little bit longer.
                </Card.Text>
              </Card.Body>
            </Card>
            {/* <img
              className="d-block w-100"
              src={scissors}
              alt="scissors"
            />
            <h3>Scissors Cut And Style</h3> */}
          </Col>
          <Col lg={3}>
            <img
              className="d-block w-100"
              src={razor}
              alt="razor"
            />
            <h3>Beard Trim With Razor</h3>
          </Col>
          <Col lg={3}>
            <img
              className="d-block w-100"
              src={scissors}
              alt="scissors"
            />
            <img
              className="d-block w-100"
              src={razor}
              alt="razor"
            />
            <h3>Both</h3>
          </Col>
        </Row>
      </Container>
    </Styles>
  );



}

export default Services;
