import React from 'react';
import styled from 'styled-components';
import { Button } from 'react-bootstrap';
import { breakpoints } from 'styled-bootstrap-responsive-breakpoints';

const Styles = styled.div`

.btn-lg {
    font-size: 3vw;
    background: none;
  }

  .Button {
    color: #fff;
    cursor: pointer;
    //display: block;
    //font-family: 'Open Sans', sans-serif;
    margin: 0 0 2em;
    //max-width: 160px; 
    position: relative;
    text-decoration: none;
    text-transform: uppercase;
    width: 100%;  
  }

  .btn-4 {
    border: 1px solid;
    overflow: hidden;
    position: relative;
    
    span {
      z-index: 20;
    }
    
    &:after {
      background: #fff;
      content: "";
      height: 155px;
      left: -75px;
      opacity: .2;
      position: absolute;
      top: -50px;
      transform: rotate(35deg);
      transition: all 550ms cubic-bezier(0.19, 1, 0.22, 1);
      width: 50px;
      z-index: -10;
    }
  }
  
  .btn-4:hover {
    
    &:after {
      left: 120%;
      transition: all 550ms cubic-bezier(0.19, 1, 0.22, 1);
    }
  }
  
  @media(max-width: ${breakpoints.md}) {

    .btn-lg {
      font-size: 5vw;
    }
  }
`

function BookButton() {

    return (
        <Styles>
            <Button className="Button btn-4" block size="lg" href="#">Book your visit now! </Button> 
        </Styles>
    );
}

export default BookButton;
