import React, { useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import * as dateFns from 'date-fns';
import { breakpoints } from 'styled-bootstrap-responsive-breakpoints';
import { Button, Form } from 'react-bootstrap';


const Styles = styled.div`

`

export default function BookForm(props) {

    const [state, setState] = useState({
        customerName: "",
        customerPhone: "",
        date: props.date,
        hourKey: props.id,
        day: props.day
    });

    // if (state.day.length > 0) {
    //     console.log(state.day);
    // }
    //console.log(state.day);
    let handleChange = event => {
        const { value, name } = event.target;

        setState (prevValue => {
            if (name === "customerName") {
                return {
                    customerName: value,
                    customerPhone: prevValue.customerPhone,
                    date: prevValue.date,
                    hourKey: prevValue.hourKey,
                    day: prevValue.day
                }
            } else {
                return {
                    customerName: prevValue.customerName,
                    customerPhone: value,
                    date: prevValue.date,
                    hourKey: prevValue.hourKey,
                    day: prevValue.day
                }
            }
        });
    }

    let handleSubmit = e => {
        e.preventDefault();

        setState ({state});

        //if (state.day.length > 0) {

        const contactData = {
            name: state.customerName,
            phone: state.customerPhone,
            date: state.date,
            key: state.hourKey,
            dayId: state.day[0]._id
        }
        
        console.log(contactData);
        // if (props.day.date === undefined) {

        axios.post('http://localhost:5000/barber/', contactData)
      .then(res => console.log(res.data));
    //}
    // } else {

    // }
      //window.location = '/';
    }

    return (
        <Styles>
            <Form onSubmit={handleSubmit}>
                <Form.Group controlId="formBasicName">
                    <Form.Label>Your name</Form.Label>
                    <Form.Control 
                        type="text"
                        name="customerName" 
                        placeholder="Name" 
                        onChange={handleChange}
                        value={state.customerName}                        
                    />
                </Form.Group>
                <Form.Group controlId="formBasicPhone">
                    <Form.Label>Your phone number</Form.Label>
                    <Form.Control
                        type="text" 
                        name="customerPhone" 
                        placeholder="Enter phone"
                        onChange={handleChange} 
                        value={state.customerPhone} 
                    />  
                </Form.Group>
                <Button 
                    variant="primary" 
                    type="submit"
                >
                    Book visit!     
                </Button>
            </Form>
        </Styles>
    );
}
