import React, { useState } from 'react';
import styled from 'styled-components';
import * as dateFns from 'date-fns';
import { breakpoints } from 'styled-bootstrap-responsive-breakpoints';
import { Container, Row, Col } from 'react-bootstrap';
import Hours from './Hours';

const Styles = styled.div`

`

let renderHeader = () => {
    const dateFormat = "MMMM yyyy";

    return (
        <Container>
            <Row className="text-center">
                <Col>
                    <i className="arrow far fa-caret-square-left" onClick={prevMonth}></i>
                </Col>
                <Col>
                    <span>
                        {dateFns.format(this.state.currentMonth, dateFormat)}
                    </span>
                </Col>
                <Col>
                    <i className="arrow far fa-caret-square-right" onClick={nextMonth}></i>
                </Col>
            </Row>
        </Container>
    );
}

let renderDays = () => {
    const dateFormat = "iiii";
    const days = [];

    let startDate = dateFns.startOfWeek(state.currentMonth, { weekStartsOn: 1 });

    for (let i = 0; i < 7; i++) {
        days.push(
            <Col key={i}>
                {dateFns.format(dateFns.addDays(startDate, i), dateFormat)}
            </Col>
        );
    }

    return (
        <Container>
            <Row className="text-center">{days}</Row>
        </Container>
    );

}

let renderCells = () => {
    const { currentMonth, selectedDate } = this.state;
    const monthStart = dateFns.startOfMonth(currentMonth);
    const monthEnd = dateFns.endOfMonth(monthStart);
    const startDate = dateFns.startOfWeek(monthStart, { weekStartsOn: 1 });
    const endDate = dateFns.endOfWeek(monthEnd, { weekStartsOn: 1 });

    const dateFormat = "d";
    const rows = [];

    let days = [];
    let day = startDate;
    let formattedDate = "";


    for (day; day <= endDate; day++) {
        for (let i = 0; i < 7; i++) {
            formattedDate = dateFns.format(day, dateFormat);
            const cloneDay = day;
            days.push(
                <Col
                className={` ${dateFns.isSameDay(day, selectedDate) 
                    ? "text-danger" 
                    : dateFns.isSameMonth(day, monthStart) ? "" : "text-muted"}`}
                               
                    key={day}
                    onClick={() => onDateClick(cloneDay)}
                    
                >
                   {/* {console.log(state)} */}
                    <span>{formattedDate}</span>
                </Col>
            );
            day = dateFns.addDays(day, 1);
        }
        rows.push(
            <Row className="text-center" key={day}>
                {days}
            </Row>
        );
        days = [];
    }
    return <Container>{rows}</Container>
}



let onDateClick = day => {
    setState({
        selectedDate: day,
        // hoursWindow: "d-block"
    });
 }

let nextMonth = () => {
    setState({
        currentMonth: dateFns.addMonths(state.currentMonth, 1)
    });
}

let prevMonth = () => {
    if (dateFns.differenceInCalendarMonths(state.currentMonth, currentDate) > 0) {
        setState({
            currentMonth: dateFns.subMonths(state.currentMonth, 1)
        });
    }
}


export default function Calendar() {

    const [state, setState] = useState({
        currentMonth: new Date(),
        selectedDate: new Date()
       // hoursWindow: "d-none"
    });
    
    const currentDate = new Date();
    
    

    return (
        <Styles>
            {renderHeader()}
            {renderDays()}
            {renderCells()}
            {/* <Hours class={state.hoursWindow} /> */}
        </Styles>
    );
}
