import React from 'react';
import styled from 'styled-components';
import * as dateFns from 'date-fns';
import { breakpoints } from 'styled-bootstrap-responsive-breakpoints';
import { Container, Row, Col } from 'react-bootstrap';


const Styles = styled.div`

@media (min-width: ${breakpoints.xs}) {

h4 {
    font-family: 'Anton', sans-serif;
    //font-size: 1.25em;
}
}

@media (min-width: ${breakpoints.lg}) {

    h4 {
        font-size: 3em;
    }
        
`


export default function CalendarHeader(props) {

    return (
        <Styles>
            <Container>
                <Row className="text-center align-items-center mb-md-3">
                    <Col>
                        <i class="fa fa-chevron-left" aria-hidden="true" onClick={props.prevMonth}></i>
                    </Col>
                    <Col>
                        <span>
                            <h4>{dateFns.format(props.actualMonth, "MMMM yyyy")}</h4>
                        </span>
                    </Col>
                    <Col>
                        <i class="fa fa-chevron-right" aria-hidden="true" onClick={props.nextMonth}></i>
                    </Col>
                </Row>
            </Container>
        </Styles>
    );
}
