import React, { useState } from 'react';
import styled from 'styled-components';
import * as dateFns from 'date-fns';
import { breakpoints } from 'styled-bootstrap-responsive-breakpoints';
import { Tab, Row, Col, ListGroup, Form } from 'react-bootstrap';


const Styles = styled.div`

`

export default function Hours() {

    const hoursRanges = ["9.00 - 9.30", "9.30 - 10.00", "10.00 - 10.30", "10.30 - 11.00", "11.00 - 11.30",
        "11.30 - 12.00", "12.00 - 12.30", "12.30 - 13.00", "13.00 - 13.30", "13.30 - 14.00", "14.00 - 14.30",
        "14.30 - 15.00", "15.00 - 15.30", "15.30 - 16.00", "16.00 - 16.30", "16.30 - 17.00", "17.00 - 17.30"];

    // let hoursRanges = () => {
    //     let arr = [];
    //     for (let i = 9; i < 18; i++) {
    //        arr.push(((9 + i/3).floor().toString()) + "." + (i * )) 
    //     }
    // }
    let hoursList = [];

    for (let i = 0; i < hoursRanges.length; i++) {
        hoursList.push({
            hour: (
            <ListGroup.Item
                key={i.toString()}
                action
                href={"#" + i.toString()}
            >
                {hoursRanges[i]}
            </ListGroup.Item>
        ),
        
            form: (
            <Tab.Pane eventKey={"#" + i.toString()}>
                Jestem form {i}
            </Tab.Pane>
        )
    })
    }


    // allBookings = [];

    // dailyBookedHours = [];

    // function dailyBookings(key) {
    //     dailyBookedHours[key] = true;
    // }

    return (
        <Styles className="" >
            <Tab.Container id="list-group-tabs-example" defaultActiveKey="#link1">
                <Row>
                    <Col sm={4}>
                        <ListGroup>
                            {hoursList.map(item => {return item.hour})}
                        </ListGroup>
                    </Col>
                    <Col sm={8}>
                        <Tab.Content>
                            {hoursList.map(item => {return item.form})}
                        </Tab.Content>
                    </Col>
                </Row>
            </Tab.Container>
        </Styles>
    );
}
