import React from 'react';
import SectionHeader from './SectionHeader';
import { Container, Col, Row, Card } from 'react-bootstrap';
import scissors from '../pictures/Background.png';
import razor from '../pictures/straight-razor.png';
import back from '../pictures/abstract-art-black.jpg';
import styled from 'styled-components';
import { breakpoints } from 'styled-bootstrap-responsive-breakpoints';

const Styles = styled.div`
h3, h5 .card {
  color: black;
}

.card {
  img {
    width: 20em;
    margin: auto;
  }
}

#services {
  position: relative;
}

#content {
  position: absolute;
  z-index: 2;
  
  width: 100%;
  justify-content: center;
  
}

#opacity {
  background: #222831;
  position: absolute;
  z-index: 1;
  width: 100%;
  height: 100%;
  opacity: 1; 
}

.bg {
  background: #393e46;
  border: 2px solid red;
  width: 70vw;
  margin: auto;
  
}

.prices {
  margin: 5em 0;
  
  border: 2px solid red;
}
`

function Services() {
  return (
    <Styles id="services">
      <div id="opacity"></div>
      <div id="content">
        <SectionHeader name="Services" />
        <div className="bg">
          <Container className="prices" fluid={true}>
            <Row className="justify-content-center">
              <Col lg={3}>
                {/* <Card>
                <Card.Img variant="top" src={scissors} />
                <Card.Body>
                  <Card.Title>Scissors Cut And Style</Card.Title>
                  <Card.Text>
                    This is a longer card with supporting text below as a natural lead-in to
                    additional content. This content is a little bit longer.
                </Card.Text>
                </Card.Body>
              </Card> */}
                <img
                  className="d-block w-100"
                  src={scissors}
                  alt="scissors"
                />
                <h3>Scissors Cut And Style</h3>
              </Col>
              <Col lg={3}>
                <img
                  className="d-block w-100"
                  src={razor}
                  alt="razor"
                />
                <h3>Beard Trim With Razor</h3>
              </Col>
              <Col lg={3}>
                <img
                  className="d-block w-100"
                  src={scissors}
                  alt="scissors"
                />
                <img
                  className="d-block w-100"
                  src={razor}
                  alt="razor"
                />
                <h3>Both</h3>
              </Col>
            </Row>
          </Container>
        </div>
      </div>
    </Styles>
  );



}

export default Services;
