import React from 'react';
import styled from 'styled-components';
import { breakpoints } from 'styled-bootstrap-responsive-breakpoints';
import Socials from "./Socials";
import MiniLogo from "./MiniLogo";
import { Container, Row } from 'react-bootstrap';

const Styles = styled.div`

background: #eeeeee;
height: 12em;

@media (min-width: ${breakpoints.xs}) {
    .ft {
    top: 1.5em;
  }
    .small-p {
    top: 1.75em;
  }
}

@media (min-width: ${breakpoints.lg}) {
  .ft {
    top: 2.5em;
  }
}
`

function Footer() {
  return (
    <Styles >
      <Container className="ft d-flex justify-content-around align-items-center position-relative">
        <Row>
          <Col>
            <MiniLogo />
          </Col>
          <Col>
            <p className="d-none d-lg-inline-block m-0 text-center">© Copyright 2020 Barber Andrew</p>
          </Col>
          <Col>
            <Socials />
          </Col>
        </Row>
      </Container>
      <p className="small-p text-center d-block d-lg-none position-relative m-0">© Copyright 2020 Barber Andrew</p>
    </Styles>
  );



}

export default Footer;
