import React from 'react';
import styled from 'styled-components';
import logo from '../pictures/head.png';
import { breakpoints } from 'styled-bootstrap-responsive-breakpoints';

const Styles = styled.div`

background: #eeeeee;

.my-icon {
    color: black;
    &:hover {
      color: #222831;
    }
}

@media (min-width: ${breakpoints.xs}) {
  height: 12em;
  img {
    width: 5em;
  }

  .my-icon {
    font-size: 3em;
  }
 
}
`

function Footer() {
  return (
    <Styles className="d-flex justify-content-between align-items-center">
      <div className="text-center px-5">
        {/* <h1 className="mini-logo">Barber Andrew</h1> */}
        <img
          className=""
          src={logo}
          alt="logo"
        />
      </div>
      <div className="text-center">
        <p>Connect me on:</p>
        <a href="#face"><i class="my-icon fab fa-facebook-square"></i></a>
        <a href="#insta"><i class="my-icon fab fa-instagram"></i></a>
        <a href="#twit"><i class="my-icon fab fa-twitter-square"></i></a>
      </div>
      

      

    </Styles>
  );



}

export default Footer;
