import React from 'react';
import styled from 'styled-components';
import * as dateFns from 'date-fns';
import { breakpoints } from 'styled-bootstrap-responsive-breakpoints';
import { Container, Row, Col } from 'react-bootstrap';

const Styles = styled.div`
    background: #343c4b;
    color: #fff;
    //text-shadow: 0.1em 0.1em #000;
`

export default function CalendarWeekDays(props) {

    const days = [1, 2, 3, 4, 5, 6, 7].map(i =>
        <Col className="px-0 pt-2" key={i}>
            <h5 className="font-weight-bold">{dateFns.format(dateFns.setDay(props.actualMonth, i), "iiiiii")}</h5>
        </Col>
    );
    return (
        <Styles>
            <Container className="">
                <Row className="text-center">{days}</Row>
            </Container>
        </Styles>
    );

}
