import React from 'react';
import styled from 'styled-components';
import logo from '../pictures/head.png';
import { breakpoints } from 'styled-bootstrap-responsive-breakpoints';

const Styles = styled.div`

background: #eeeeee;
height: 19em;

img {
  width: 7em;
}

@media (min-width: ${breakpoints.xs}) {

  height: 70vw;
}
`

function Footer() {
  return (
    <Styles className="d-flex justify-content-center align-items-center">
      <div className="text-center px-5">
        <h1 className="mini-logo">Barber Andrew</h1>
        <img
          className=""
          src={logo}
          alt="logo"
        />
      </div>
      <div className="">
        <p>Connect me on:</p>
        <a href="#face"><i class="fab fa-facebook-square"></i></a>
        <a href="#insta"><i class="fab fa-instagram"></i></a>
        <a href="#twit"><i class="fab fa-twitter-square"></i></a>
      </div>
      

      

    </Styles>
  );



}

export default Footer;
